import { getClient } from '../helpers';
export async function clientOf(server, config) {
    const index = await getClient(config.directory, config.client);
    const target = config.api + '/';
    return (req, res) => {
        if (req.url.endsWith('/')) {
            res.sendFile(index);
        }
        else {
            res.redirect(target);
        }
    };
}
